import React, {useState, useEffect} from 'react'

import { Container, Row, Col } from 'reactstrap'
import CommonSection from '../shared/CommonSection'
import '../styles/places.css'

// import tourData from '../assets/data/tours'
import TourCard from '../shared/TourCard'
import Newsletter from '../shared/Newsletter'
import SearchBar from '../shared/SearchBar'
import useFetch from '../hooks/useFetch'
import { BASE_URL } from '../utils/config'

const Places = () => {

  const [pageCount, setPageCount]= useState(0);
  const [page, setPage]= useState(0);

  const {data:tours, loading, error}= useFetch(`${BASE_URL}/tours`)
  const {data:touCount}=useFetch(`${BASE_URL}/tours/search/getTourCount`)


  useEffect(() =>{
    const pages= Math.ceil(touCount/4);
    setPageCount(pages);
  }, [page, touCount])







  return (
  <>
  <CommonSection title={"All Places"} />
  <section>
    <Container>
      <Row>
        <SearchBar />
      </Row>
    </Container>
  </section>
  <section className='pt-0'>
    <Container>
      {
        !loading && !error &&<Row>
        {
          tours?.map(tour=> (
          <Col lg="3" className='mb-4' key={tour.id}>
            {" "} 

          <TourCard tour={tour}/></Col>))
        }
        <Col lg="12">
          <div className="pagination d-flex align-items-center justify-content-center mt-4 gap-3">
            {[ ...Array(pageCount).keys()].map(number=>(
              <span key={number} onClick={()=> setPage(number)}
              className={page===number? "active__page": ""}

              >
                {number + 1}
              </span>

            ))}


          </div>
        </Col>
      </Row>
      }
    </Container>
  </section>
  <Newsletter/>
  </>
  )
}

export default Places
